import React, { useState } from 'react';
import FindASpaceScreen from './screens/FindASpaceScreen';
import FindACommunityScreen from './screens/FindACommunityScreen';
import BuddingCommunitiesScreen from './screens/BuddingCommunitiesScreen';
import Navigation from './components/Navigation';

function App() {
  const [currentPage, setCurrentPage] = useState<'create' | 'communities' | 'budding'>('create');

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#FEFCF3' }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '20px' }}>
        {/* App Header */}
        <div style={{ textAlign: 'center', marginBottom: '32px' }}>
          <h1 style={{ 
            fontSize: '3rem', 
            fontWeight: 'bold', 
            color: '#1f2937', 
            marginBottom: '8px' 
          }}>
            ThirdSpace Boston
          </h1>
          <div style={{ 
            fontSize: '1.125rem', 
            color: '#4B5563', 
            marginBottom: '0',
            lineHeight: '1.8',
            maxWidth: '900px',
            margin: '0 auto'
          }}>
            <p style={{ margin: '0 0 16px 0' }}>
              ThirdSpace Boston connects communities to local <strong style={{ color: '#1F2937' }}>3rd spaces</strong>, like the neighborhood coffee shop or library, by renting their space during off-hours. 
            </p>
            <p style={{ margin: '0 0 16px 0' }}>
              Use <strong style={{ color: '#3B82F6' }}>"Create a Community"</strong> to propose a group; pending groups appear in <strong style={{ color: '#3B82F6' }}>"Budding Communities"</strong> where members can join and pledge weekly funds. 
            </p>
            <p style={{ margin: '0' }}>
              Once funded, the community moves to <strong style={{ color: '#3B82F6' }}>"Existing Communities"</strong>. Join active communities and browse events to start connecting!
            </p>
          </div>
        </div>

        {/* Navigation - Updated */}
        <Navigation currentPage={currentPage} onPageChange={setCurrentPage} />

        {/* Page Content */}
        {currentPage === 'create' && <FindASpaceScreen />}
        {currentPage === 'communities' && <FindACommunityScreen />}
        {currentPage === 'budding' && <BuddingCommunitiesScreen />}
      </div>
    </div>
  );
}

export default App;
